import { hexToRgb } from "./rgba";

const BRIGHTNESS_TRESHOLD = 200;

// https://www.w3.org/TR/AERT/#color-contrast
const calculateBrightness = (r: number, g: number, b: number) => {
  return (r * 299 + g * 587 + b * 114) / 1000;
};

export const getBrightnessRGB = (r: number, g: number, b: number) => {
  const brightness = calculateBrightness(r, g, b);
  if (brightness > BRIGHTNESS_TRESHOLD) {
    return "light";
  } else {
    return "dark";
  }
};

export const getBrightnessHEX = (hex: any) => {
  const rgb = hexToRgb(hex);
  return rgb ? getBrightnessRGB(rgb.r, rgb.g, rgb.b) : "dark";
};
