export function idFromDate(): string {
  return (+new Date()).toString(36).slice(-7);
}

export function idFromRandom(): string {
  return crypto.getRandomValues(new Uint8Array(7)).reduce((id, byte) => {
    byte &= 63;
    if (byte < 36) {
      id += byte.toString(36);
    } else if (byte < 62) {
      id += (byte - 26).toString(36).toUpperCase();
    } else if (byte > 62) {
      id += "-";
    } else {
      id += "_";
    }
    return id;
  }, "");
}
